<h2><span class="glyphicon glyphicon-time"></span> Comércio</h2><div id="comercio" class="carousel slide" data-ride="carousel">    <!-- Wrapper for slides -->    <div class="carousel-inner" role="listbox">        <?php foreach ($comercio as $key => $value) :            $class = ($key == 0) ? "item active" : "item"; ?>            <div class='<?= $class ?>'>                <div class='row' style='margin:0'>                    <div class='noticia'>                        <div class='square'>                            <div class='<?= ($dia == "!") ? "block today" : "block" ?> amarelo'>                                <div class='centered'>                                    <h2><?= $value->Dia ?></h2>                                    <h5><?= $value->Mes ?></h5>                                </div>                            </div>                        </div>                        <div class='noticia-descricao'>                            <strong style="text-transform: capitalize"><?= $value->Semana ?></strong>                            <div class='marcador-agenda'>                                <?= $value->DS_Comercio ?>                                <br>                                <span class='glyphicon glyphicon-time'></span>                                <?= $value->Inicio ?>                                -                                <?= $value->Termino ?>                            </div>                        </div>                    </div>                </div>            </div>        <?php endforeach ?>    </div></div><div class="text-center"><a href="#comercio" role="button" data-slide="prev">    <button type="button" class="btn btn-warning">    <span class="glyphicon glyphicon-triangle-left" aria-hidden="true"></span>    <span class="sr-only">Anterior</span>    </button></a><a href="#comercio" role="button" data-slide="next">    <button type="button" class="btn btn-warning">    <span class="glyphicon glyphicon-triangle-right" aria-hidden="true"></span>    <span class="sr-only">Próximo</span>    </button></a></div>